INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('8e2ddb12-1367-4336-c478-3d3beaf3e4f1', '0ccd8930-9d1b-4935-f2b0-30a519fa0542', 'Artificer Armour', 10, '**^^Adeptus Astartes^^** model only. The bearer has a Save characteristic of 2+ and the Feel No Pain 5+ ability.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('3b5045ae-aa1e-4e7b-40f0-52e5c912d852', '0ccd8930-9d1b-4935-f2b0-30a519fa0542', 'The Honour Vehement', 15, '**^^Adeptus Astartes^^** model only. Add 1 to the Attacks and Strength characteristics of the bearer’s melee weapons. While the bearer is under the effects of the Assault Doctrine, add 2 to the Attacks and Strength characteristics of the bearer’s melee weapons instead.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('d1a60bf1-2bee-7e7d-9747-24e31dae6923', '0ccd8930-9d1b-4935-f2b0-30a519fa0542', 'Adept of the Codex', 20, 'Captain model only. At the start of your Command phase, if the bearer is on the battlefield, instead of selecting a Combat Doctrine to be active for your army, you can select the Tactical Doctrine. If you do, until the start of your next Command phase, that doctrine is active for the bearer’s unit only, even if you have already selected that doctrine to be active for your army this battle.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
