INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('25d3216a-0eff-e8ac-44aa-0dfe3e1ed424', '9f7c3840-0b7c-face-ee6a-35dd8ca20220', 'Black Spear Task Force', 'Kill Teams: Each time an attack targets a KILL TEAM unit from your army that contains models with different Toughness characteristics, until the attacking unit has finished making its attacks, use the Toughness characteristic of the majority of the models in that unit when determining what result is required for that attack to successfully wound. If two or more Toughness characteristics are tied for majority, use the highest Toughness characteristic.
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('2db2747f-f290-0722-9329-ad41c753246c', '25d3216a-0eff-e8ac-44aa-0dfe3e1ed424', 'Thief of Secrets', 25, '**^^Adeptus Astartes^^** model only. Improve the Strength, Damage and Armour Penetration characteristics of the bearer’s melee weapons by 1. At the end of the Fight phase, if one or more enemy models were destroyed as a result of a melee attack made by the bearer this phase, until the end of the battle, improve the Strength, Damage and Armour Penetration characteristics of the bearer’s melee weapons by 2 instead.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('7c9afb31-3eab-4014-e3e5-7f860f7fb748', '25d3216a-0eff-e8ac-44aa-0dfe3e1ed424', 'Beacon Angelis', 25, 'ADEPTUS ASTARTES model only. Models in the bearer’s unit have the Deep Strike ability. In addition, you can target the bearer’s unit with the Rapid Ingress Stratagem for 0CP.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('b4c519b3-af69-2427-b5f9-e2354d30e1ff', '25d3216a-0eff-e8ac-44aa-0dfe3e1ed424', 'Osseus Key', 15, 'WATCH MASTER or TECHMARINE model only. At the start of your opponent’s Shooting phase, select one enemy VEHICLE unit (excluding TITANIC units) within 12" of and visible to the bearer. That model must take a Leadership test. If that test is passed, until the end of the phase, each time a model in that unit makes an attack, subtract 1 from the Hit roll; if that test is failed, that unit is not eligible to shoot this phase.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
