INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('a2673302-0d96-e41d-4437-6553ae265544', '9f7c3840-0b7c-face-ee6a-35dd8ca20220', 'Liberator Assault Group', 'The Red Thirst: Each time an ^^**Adeptus Astartes**^^ unit from your army is selected to fight, if that unit made a Charge move this turn, until the end of the phase, add 2 to the Strength characteristic and add 1 to the Attacks characteristic of melee weapons equipped by models in that unit.')
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('bad29499-a17d-a562-950b-9a6477e83460', 'a2673302-0d96-e41d-4437-6553ae265544', 'Icon of the Angel', 20, 'Adeptus Astartes model only. Each time an enemy unit (excluding Monsters and Vehicles) within Engagement Range of the bearer’s unit is selected to Fall Back, models in that enemy unit must take Desperate Escape tests as if their unit was Battle-shocked. When doing so, if that enemy unit is also Battle-shocked by other means, subtract 1 from each of those Desperate Escape tests.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('815b08f0-26fe-28d3-5b00-bdab0ffd6132', 'a2673302-0d96-e41d-4437-6553ae265544', 'Speed of the Primarch', 25, 'Adeptus Astartes model only. Once per battle, at the start of the Fight phase, the bearer can use this Enhancement. If it does, until the end of the phase, models in the bearer''s unit have the Fights First ability');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
