INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('f45f1cf6-bd8f-8af1-6cfd-fe79baec6b14', 'a2673302-0d96-e41d-4437-6553ae265544', 'Gift of Foresight', 15, 'Adeptus Astartes model only. Once per battle round, just after making a Hit roll, a Wound roll or a saving throw for the bearer, you can treat the result as a unmodified 6 instead.');
INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('6e2fdf28-2348-2cea-bd91-4eaf1da6ab5a', '9f7c3840-0b7c-face-ee6a-35dd8ca20220', 'Anvil Siege Force', 'Shield of the Imperium: Ranged weapons equipped by **^^Adeptus Astartes^^** models from your army have the **[HEAVY]** ability. If such a weapon already has this ability, each time an attack is made with that weapon, if the attacking model''s unit Remained Stationary this turn, add 1 to the Wound roll.')
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
