INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('09cd2c1f-9fdc-e64b-3ac1-4c16b0741dc4', '6e2fdf28-2348-2cea-bd91-4eaf1da6ab5a', 'Stoic Defender', 15, 'Adeptus Astartes model only. While the bearer is leading a unit, models in that unit have the Feel No Pain 6+ ability while they are within range of an objective marker you control and, while that unit is Battle-shocked, halve the Objective Control characteristic of models in that unit instead of changing it to 0');
INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('45ef971c-66d8-ef57-1c3d-23acf90786ee', '9f7c3840-0b7c-face-ee6a-35dd8ca20220', 'Ironstorm Spearhead', 'Armoured Wrath: Once per phase for each ^^**Adeptus Astartes^^** unit in your army, you can re-roll one Hit roll, one Wound roll, or one Damage roll made for a model in that unit.')
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('ff2f76b7-cf1d-b736-5be8-0e216f0d65fa', '45ef971c-66d8-ef57-1c3d-23acf90786ee', 'Adept of the Omnissiah', 35, '**^^Techmarine^^** model only. Once per battle round, when a saving throw is failed for a friendly **^^Adeptus Astartes Vehicle^^** model within 6" of the bearer, you can change the Damage characteristic of that attack to 0.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
