INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('6b50c166-a342-869a-68f4-af66c0b2451a', '9f7c3840-0b7c-face-ee6a-35dd8ca20220', 'Firestorm Assault Force', 'Close-Range Eradication: Ranged weapons equipped by Adeptus Astartes models from your army have the [ASSAULT] ability, and each time an attack made such a weapon targets a unit within 12", add 1 to the Strength characteristic of that attack.')
