INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('11278e94-7ac8-bd0d-bc5c-91e6e9783073', '9f7c3840-0b7c-face-ee6a-35dd8ca20220', 'Vanguard Spearhead', 'Shadow Masters: Each time a ranged attack targets an ^^**Adeptus Astartes**^^ unit from your army, unless the attacking model is within 12", subtract 1 from the Hit roll and the target has the Benefit of Cover against that attack.')
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('b13bb95d-cd65-5e27-39ac-348be5a31b40', '11278e94-7ac8-bd0d-bc5c-91e6e9783073', 'Execute and Redeploy', 20, 'Phobos model only. In your Shooting phase, after the bearer''s unit has shot, if that unit is not within Engagement Range of one or more enemy units, it can make a Normal move of up to 6". If it does until the end of the turn, that unit is not eligible to delcare a charge. This cannot allow the bearer''s unit to move more than once in your Shooting phase.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
