INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('5b4295a4-2242-2ee7-faf3-e00f23d96bea', '0ef5ce6c-a9e1-fd4f-edf2-93e7fed844c0', 'Saintly Example', 10, 'ADEPTA SORORITAS model only. When the bearer is destroyed, you gain an additional D3 Miracle dice.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('10bea226-8f1e-bb46-bf8a-e465c8b50a96', '0ef5ce6c-a9e1-fd4f-edf2-93e7fed844c0', 'Through Suffering, Strength', 25, '**^^Adepta Sororitas^^** model only. Add 1 to the Attacks, Strength and Damage characteristics of the bearer’s melee weapons. If the bearer has lost one or more wounds, add 2 to the Attacks, Strength and Damage characteristics of the bearer’s melee weapons instead.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('992daf85-ba60-5608-c91e-a3e43ff6111e', '0ef5ce6c-a9e1-fd4f-edf2-93e7fed844c0', 'Chaplet of Sacrifice', 25, 'ADEPTA SORORITAS model only. At the end of your Command phase, if the bearer is on the battlefield, you can re-roll one Miracle dice from your Miracle dice pool and return it to your Miracle dice pool with the new result you rolled. When doing so, if the bearer''s unit is below its Starting Strength, you can re-roll up to 3 Miracle dice in this way instead.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('32e0c9ba-ad8d-aebc-2a0a-d1019e401814', '0ef5ce6c-a9e1-fd4f-edf2-93e7fed844c0', 'Mantle of Ophelia', 20, '**^^Canoness^^** or **^^Palatine^^** model only. Each time an attack is allocated to the bearer, change the Damage characteristic of that attack to 1.');
