INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('c26bd355-994c-8c34-6fe5-6a08cb7e4362', '77e29b0c-186c-eaa5-e2bc-ba72f218bf00', 'Catechism of Divine Penitence', 20, 'CANONESS, PALATINE, or MINISTORUM PRIEST model only. The bearer gains the PENITENT keyword and, during the Declare Battle Formations step, can be attached to a REPENTIA SQUAD unit.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('01a31830-fa2e-0bad-f50f-8652588f873e', '77e29b0c-186c-eaa5-e2bc-ba72f218bf00', 'Psalm of Righteous Judgement', 30, 'ADEPTA SORORITAS model only. While the bearer is on the battlefield, each time an enemy unit is destroyed by a PENITENT unit from your army, you can discard 1 Miracle dice to gain 1 Miracle dice showing a value of 6.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('1efc71ab-9956-9222-9d97-bfb99913ac42', '77e29b0c-186c-eaa5-e2bc-ba72f218bf00', 'Verse of Holy Piety', 15, 'PENITENT model only. Once per battle, at the start of the battle round, select one Vow of Atonement. Until the start of the next battle round, that Vow of Atonement is active for the bearer''s unit in addition to any that is active for your army, even if you have already selected that Vow of Atonement this battle.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('84b17038-7cba-4f64-161f-fde2361f8e1c', '77e29b0c-186c-eaa5-e2bc-ba72f218bf00', 'Refrain of Enduring Faith', 25, 'PENITENT model only. While the bearer is leading a unit, models in that unit have a 5+ invulnerable save.');
