INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('11dd5d94-9802-f985-4497-163b5c82507c', 'd8a28294-02ab-ad7a-4620-86b701d29802', 'Iron Surplice of Saint Istalela', 10, 'CANONESS or PALATINE model only. The bearer has a Save characteristic of 2+ and the Feel No Pain 5+ ability.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('3a988a5c-edbb-2eb3-b2af-1c03cc08d02a', 'd8a28294-02ab-ad7a-4620-86b701d29802', 'Righteous Rage', 15, '**^^Adepta Sororitas^^** model only. Each time the bearer is selected to fight, you can first discard up to 3 Miracle dice. For each Miracle dice just discarded, until the end of the phase, add 1 to the Attacks and Strength characteristics of the bearer''s melee weapons.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('7ff73d35-a914-563f-717b-afeb79b36d95', 'd8a28294-02ab-ad7a-4620-86b701d29802', 'Manual of Saint Griselda', 20, 'ADEPTA SORORITAS model only. At the start of your Command phase, you can discard up to 2 Miracle dice. Then, add 1 Miracle dice to your Miracle Dice pool showing a value equal to the sum of the two Miracle dice you discarded (to a maximum of 6).');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('61362041-f34d-8b28-8d09-1c35ef60b0a8', 'd8a28294-02ab-ad7a-4620-86b701d29802', 'Fire and Fury', 30, '**^^Adepta Sororitas^^** model only. While the bearer is leading a unit, add 1 to the Attacks characteristic of Torrent weapons equipped by models in that unit, and all other ranged weapons equipped by models that unit have the **[SUSTAINED HITS 1]** ability.');
INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
