INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('44d43fc1-31da-f880-3862-566881395e78', '5aece78a-a51e-a422-2e85-e34c61e5e85b', 'Triptych of the Macharian Crusade', 20, 'ADEPTA SORORITAS model only. Each time the bearer uses an Act of Faith to substitute a saving throw, that saving throw is successful, irrespective of the value of the Miracle dice used.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('e40eb686-74ff-c3ba-90f6-673190fd4c07', '5aece78a-a51e-a422-2e85-e34c61e5e85b', 'Blade of Saint Ellynor', 15, 'ADEPTA SORORITAS model only. Improve the Strength and Armour Penetration characteristics of the bearer''s melee weapons by 1, and those weapons have the [Precision] ability. In addition, each time the bearer fights, if one or more enemy models is destroyed by those attacks, you gain 1 Miracle dice.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('704272e1-01c3-1561-aafb-6dc0774129a9', '5aece78a-a51e-a422-2e85-e34c61e5e85b', 'Litanies of Faith', 10, 'CANONESS or PALATINE model only. At the start of your Command phase, if the bearer is on the battlefield, take a Leadership test for the bearer. If that test is passed, you gain 1 Miracle dice.');
