INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('837a2c19-da10-305b-3605-dae9ea66a986', 'a1505a26-9220-e483-9bed-7c24e3940bbb', 'Pactbound Zealots', 'Marks of Chaos: When mustering your army, when you select a ^^Heretic Astartes^^ unit to include in your army, if that unit is not an ^^Epic Hero^^ and does not already have one of the following keywords, you must select one for that unit to gain and note it on your Army Roster: ^^Khorne, Tzeentch, Nurgle, Slaanesh, Chaos Undivided^^.
INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('45023773-79c8-8c2f-4a79-6da82af77871', 'a1505a26-9220-e483-9bed-7c24e3940bbb', 'Veterans of the Long War', 'Focus of Hatred: At the start of your Command phase, select one unit from your opponent''s army to be your focus of hatred. Until the start of your next Command phase, each time a ^^**Heretic Astartes**^^ model from your army (excluding ^^**Damned**^^ models) makes an attack that targets your focus of hatred, you can re-roll the Hit roll.')
INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('070b901a-f0ea-d9de-bd36-fd3d065048f1', 'a1505a26-9220-e483-9bed-7c24e3940bbb', 'Deceptors', 'Masters of Misdirection: In the Declare Battle Formations step, you can select a number of ^^**Legionaries**^^ and ^^**Cultist Mob**^^ units from your army; until the end of the battle, those units, and any ^^**Character**^^ units attached to them (excluding ^^**Epic Heroes**^^), have the Infiltrator ability. The maximum number of units you can select in this way depends on the battle size, as shown below.
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('9bf3fe9f-2c8d-ed8a-182c-415d4379deec', '070b901a-f0ea-d9de-bd36-fd3d065048f1', 'Falsehood', 10, 'In the Declare Battle Formations step, you can set up the bearer in Reserves instead of setting it up on the battlefield. If you do, in the Reinforcements step of one of your Movement phases, you can select one model in a friendly LEGIONARIES or CHOSEN unit that has two or more models remaining and is on the battlefield (excluding Attached units). The selected model is destroyed (ignoring any rules that are triggered when a model is destroyed) and the bearer is set up as close as possible to where that model was destroyed and only within Engagement Range of any units if the destroyed model was in Engagement Range of those units. The bearer now attaches to that unit as its Leader.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('7194fe71-0c75-3942-201b-eae9ebf835d5', '070b901a-f0ea-d9de-bd36-fd3d065048f1', 'Cursed Fang', 10, 'Improve the Armour Penetration characteristic of the bearer''s melee weapons by 1, and the bearer''s melee weapons have the [PRECISION] ability.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
