INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('ad13dba4-572f-7012-eba9-7d2e9c7e0143', '7d98221d-a454-c90f-c95e-7109b525b31c', 'Plagueveil', 25, 'While the bearer’s unit is within range of one or more objective markers that you control, that unit can only be selected as the target of a ranged attack if the attacking model is within 18".');
INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('5761f461-ce8f-7e27-37d0-f46f8007f64c', '7681fa27-38bd-e56b-fa36-6a2bb17edb46', 'Virulent Vectorium', 'Worldblight: If you control an objective marker at the end of your Command phase and a ^^**Death Guard**^^ unit from your army (excluding Battle-shocked units) is within range of that objective marker, that objective marker remains under your control until your opponent''s Level of Control over that objective marker is greater than yours at the end of a phase. In addition, until you lose control of that objective marker, it has the Nurgle''s Gift ability as if it were a ^^**Death Guard**^^ unit from your army.')
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
