INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('96a396e7-7ba3-9ab8-8075-009dfeea80af', '7a6eaa16-ddf8-994e-ffdd-599d2ec3d8bc', 'Talisman of Burgeoning', 25, 'While the bearer is leading a ^^**Poxwalkers**^^ unit, add 1 to the Toughness characteristic of ^^**Poxwalkers**^^ models in that unit.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('11b775e3-856e-e590-7d89-f307a3f06c23', '7a6eaa16-ddf8-994e-ffdd-599d2ec3d8bc', 'Witherbone Pipes', 25, 'While the bearer is leading a ^^**Poxwalkers**^^ unit, add 1 to the Objective Control characteristic of models in that unit, and each time that unit takes a Battle-shock or Leadership test, add 1 to that test.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('85c09e69-d916-7a0f-b3f2-faa82cb0ce92', '7a6eaa16-ddf8-994e-ffdd-599d2ec3d8bc', 'Sorrowsyphon', 10, 'While the bearer is leading a ^^**Poxwalkers**^^ unit, add 1 to the Damage characteristic of the bearer''s Plague Wind weapon. Each time the bearer makes one or more attacks with a Plague Wind weapon, after the bearer''s unit has resolved its attacks, D3 Bodyguard models from the bearer''s unit are destroyed.');
INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('3020b9a8-4ec7-88af-d9a6-390200bb9570', '7681fa27-38bd-e56b-fa36-6a2bb17edb46', 'Death Lord''s Chosen', 'Deadly Vectors: In your opponent''s Command phase, roll 2D6 for each Afflicted enemy unit, subtracting 1 from the result if that unit is Below Half-strength. If the result is 6 or less, that enemy unit suffers D3 mortal wounds.')
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('27127c2c-61ac-ec20-6bea-e3dfd9adb7f5', '3020b9a8-4ec7-88af-d9a6-390200bb9570', 'Face of Death', 10, 'At the start of the Fight phase, each enemy unit within Engagement Range of the bearer''s unit must take a Battle-shock test.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('b82fe2b2-57f6-fbbe-d690-b7d09e70ef25', '3020b9a8-4ec7-88af-d9a6-390200bb9570', 'Vile Vigour', 15, 'While the bearer is leading a unit, add 1" to the Movement characteristics of models in that unit and you can re-roll Advance rolls made for that unit.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('800787ff-bb05-ed47-8dfb-8483b5ee053d', '3020b9a8-4ec7-88af-d9a6-390200bb9570', 'Warprot Talisman', 30, 'Once per battle, at the end of your opponent''s turn, if the bearer''s unit is not within Engagement Range of one or more enemy units, you can remove it from the battle field and place it into Strategic Reserves.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('0a641598-638b-16bd-313f-f2142af5da55', '3020b9a8-4ec7-88af-d9a6-390200bb9570', 'Helm of the Fly King', 20, 'While the bearer is leading a unit, models in that unit cannot be targeted by ranged attacks unless the attacking model is within 18".');
