INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('9e2c33ae-71c6-cd43-dbeb-67cc584a23eb', 'b6518f74-3635-a1a0-55b4-8b63ae0cf810', 'Avenger''s Crown', 15, 'If the bearer is destroyed by a melee attack, if it has not fought this phase, roll one D6: on a 2+, do not remove it from play. The destroyed model can fight after the attacking unit has finished making its attacks, and is then removed from play.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('863ee41f-d804-f786-2570-b4090ebc523f', 'b6518f74-3635-a1a0-55b4-8b63ae0cf810', 'Gateways to Glory', 10, 'Each time the bearer makes a Normal, Advance or Charge move, it can move horizontally through models and terrain features. When doing so, it can move within Engagement Range of such models but cannot end a Normal or Advance move within Engagement Range of them.');
INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('51fd61e5-e826-d4c8-5b9c-efe007ec6a8e', 'dd2936a0-6cc2-791b-ad34-8323b51b6e9d', 'Cult of Blood', 'Idols of Khorne: At the start of your Command phase, you can select one of the Idols of Khorne abilities listed below. Until the start of your next Command phase, that ability is active, and its effects apply to all ^^**World Eaters Titanic^^** and ^^**World Eaters Monster^^** units from your army. You can only select each Idols of Khorne ability once per battle.
