INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('7be62ced-8dff-7292-65e0-8294b917c279', '1489fd95-e0fd-fa01-f54f-579693aeb35c', 'Diabolical Resilience', 35, 'The bearer has the Feel No Pain 6+ ability and you can ignore any or all modifiers to its Move characteristic and/or to any Advance or Charge rolls made for it.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('1537812c-7c7d-0b76-8a12-b134319ee66d', '1489fd95-e0fd-fa01-f54f-579693aeb35c', 'Pave The Way', 15, 'At the start of the Declare Battle Formations step, you can select up to three DAMNED units from your army. The selected units have the Scouts 6" ability.');
