INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('357221af-141b-36a7-ab64-362195f7af89', '9c034e74-464c-1449-6c67-259b154003db', 'Lords of Dread', 'Tyrannical Court: Improve the Objective Control characteristic of ^^**Chaos Knight Character^^** models from your army by 2. In addition, once per battle round, if your ^^**Warlord^^** is on the battlefield, you can use the Claimed for the Dark Gods Stratagem for 0CP.')
