INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('5acd9d1c-c3c9-4402-dbaf-9ff6d09680cd', 'dde08a50-39bb-367b-95f5-c2174a31e330', 'Master Artisan', 20, '^^**Haemonculus**^^ model only. Add 1 to the bearer''s Wounds characteristic and add 1 to the Toughness characteristic of models in the bearer''s unit.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('5264a8f0-7aab-277e-a64b-de190ae60f43', 'dde08a50-39bb-367b-95f5-c2174a31e330', 'Master Repugnomancer [Aura]', 15, '^^**Haemonculus**^^ model only. Add 3" to the range of the bearer''s Fear Incarnate ability, and each time a friendly ^^**Drukhari**^^ within 9" of the bearer fails a Battle-shock test or is destroyed, roll one D6; on a 4+, you gain 1 Pain token.');
INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('02e268d1-08fc-7daf-e065-dfb4aed7c212', 'fb49b6f3-185d-e4ad-f234-abf2142cf2a2', 'Kabalite Cartel', 'Murderous Agenda: At the start of the first battle round, select one of the Contracts from below, then select one unit from your opponent''s army that matches the ''Contract'' description in the Contract. Until that Contract is completed, that unit is your Contract unit and ^^**Kabal**^^ and ^^**Blades For Hire**^^ units from your army have the ability stated in that Contract. At the start of your Command phase, if your Contract unit is destroyed, that Contract is completed and you gain 3 Pain tokens.
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('8f6105d4-fee7-acf4-f52a-9f8e08924b6b', '02e268d1-08fc-7daf-e065-dfb4aed7c212', 'Leechbite Plate', 5, '^^**Archon**^^ model only. The bearer has a Save characteristic of 3+. At the start of either player''s Command phase, you can spend 1 Pain token; if you do, the bearer regains all of its lost wounds.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('ea6bbcbb-efa4-6c70-29f0-54084d3c6000', '02e268d1-08fc-7daf-e065-dfb4aed7c212', 'Webway Awl', 25, '^^**Archon**^^ model only. Models in the bearer''s unit have the Deep Strike ability, and you can target the bearer''s unit with the Rapid Ingress Stratagem for 0CP.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('35370d84-a6f7-d870-9d41-bc2311dcbdb5', '02e268d1-08fc-7daf-e065-dfb4aed7c212', 'Informant Network', 30, '^^**Archon**^^ model only. At the start of the Declare Battle Formations step, select up to three ^^**Kabalite Warriors**^^ and/or ^^**Hand of the Archon**^^ units from your army; those units gain the Infiltrators ability.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
