INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('73e2ab66-575d-93a6-f3a1-54a7a58d1245', 'fb49b6f3-185d-e4ad-f234-abf2142cf2a2', 'Spectacle of Spite', 'Combat Drugs: At the start of your Command phase, select which Combat Drugs will be active for your army until the start of your next Command phase. To do so, either select one from the list below (you cannot select the same Combat Drug more than once per battle), or randomly select two by rolling two D6. When doing so randomly, Combat Drugs you have previously selected can become active again, but if you randomly select one that is already active for your army, it has no additional effect.
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('83112f08-7035-9a98-fa71-c99b9add4742', '73e2ab66-575d-93a6-f3a1-54a7a58d1245', 'Pharmacophex', 15, '^^**Succubus**^^ model only. At the start of your Command phase, after selecting which Combat Drugs will be active for your army, roll one D6 and consult the Combat Drugs list. The result rolled applies to the bearer''s unit until the start of your next Command phase in addition to any other Combat Drugs that are active for your army. If you randomly select one that is already active for your army, it has no additional effect.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
