INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('7933873d-a98e-2815-4050-2dc740ee4783', '340488ab-16b9-9fd5-f01c-6d0219f4a4d0', 'Nomad Strategist', 20, 'Once per battle, at the end of your opponent''s Fight phase, if the bearer is on the battlefield, it can use this Enhancement. If it does, select one ^^**Hernkyn^^** unit from your army, and, for every 2YP you just spent, select up to one additional ^^**Hernkyn^^** unit from your army. You can remove those units from the battlefield and place them into Strategic Reserves. You cannot select a unit that is within Engagement Range of one or more enemy units (excluding assailed units).');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('95fcbdd1-23a2-b8f8-26ce-2e9756d40235', '340488ab-16b9-9fd5-f01c-6d0219f4a4d0', 'Eye For Weakness', 25, 'Each time a model in the bearer''s unit makes an attack that targets an assailed unit, add 1 to the Wound roll.');
INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('1384bf6e-fde1-ec86-c4e6-c00d02b4208f', 'bdfb2412-05ad-2eea-a05a-45693e822400', 'Needgaârd Oathband', 'Martial Leverage: Each time an enemy unit is destroyed, you gain 1YP.')
INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('f8eb1b8a-93a9-6255-319d-780f6e36a9fc', 'bdfb2412-05ad-2eea-a05a-45693e822400', 'Hearthfyre Arsenal', 'Optimal Application: At the end of your Command phase, you gain 1YP for each objective marker you control that is not within your deployment zone and has one or more ^^**Iron-Master^^** and/or ^^**Memnyr Strategist^^** models from your army within range of it (to a maximum of 2YP gained from this Detachment rule per turn).
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
