INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('a61851e3-2506-85ec-1b13-7fd5837aaeb1', 'f8eb1b8a-93a9-6255-319d-780f6e36a9fc', 'Graviton Vault', 5, 'In your Shooting phase, after the bearer has shot, and in the Fight phase, after the bearer has fought, select one enemy ^^**Monster^^** or ^^**Vehicle**^^ unit hit by one or more of those attacks. Until the start of your next turn, that enemy unit is suppressed. While a unit is suppressed, each time a model in that unit makes an attack, subtract 1 from the Hit roll.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('f290bf8e-5188-12a9-dd26-5c68bacaf25b', 'f8eb1b8a-93a9-6255-319d-780f6e36a9fc', 'Mantle of Elders', 10, 'Each time the bearer''s unit is selected to shoot, if you spend YP using the Optimal Application Detachment rule when doing so, you can roll one D6: on a 2+, you gain 1YP.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
