INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('636771c5-dcf1-811a-a31d-18df9321f0e4', 'bdfb2412-05ad-2eea-a05a-45693e822400', 'Dêlve Assault Shift', 'Fury from the Dêlve: ^^**Cthonian Beserks^^** units from your army have the Deep Strike ability.')
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('be59067d-dda7-2215-3e0b-34421b8a1f5d', '636771c5-dcf1-811a-a31d-18df9321f0e4', 'Dêlvwerke Navigator', 25, 'In the Reinforcements step of your Movement phase, the bearer can use this Enhancement. If it does, select one friendly ^^**Cthonian Beserks^^** unit visible to the bearer and then spend any number of YP. Return one destroyed model to that unit, and then, for every 2YP you just spent, return up to one additional destroyed model to that unit.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('fb841ed3-cf2a-e9df-63cf-481c5fabecef', '636771c5-dcf1-811a-a31d-18df9321f0e4', 'Quake Supervisor', 20, 'While the bearer is within 3" of one or more friendly ^^**Leagues of Votann Artillery^^** units, the bearer has the Lone Operative ability, and each time one of those ^^**Artillery^^** units makes a ranged attack that targets an enemy unit visible to the bearer, add 1 to the Hit roll.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('b29d1026-e176-fbb2-be44-c0211a4feb5f', '636771c5-dcf1-811a-a31d-18df9321f0e4', 'Piledriver', 15, 'Each time the bearer''s unit is selected to fight, the bearer can use this Enhancement. If it does, spend up to 2YP and, until the end of the phase, add the number of YP you just spent to the Damage characteristic of the bearer''s melee weapons.');
