INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('4b31d52c-6917-cdff-a7ed-c44cfb692b37', 'bdfb2412-05ad-2eea-a05a-45693e822400', 'Mercenary Oathband', 'Ruthless Reinvestment: Your ^^**Leagues of Votann^^** units do not have the Hostile Acquisition or Fortify Takeover abilities except as described in this rule. At the start of the battle, your ^^**Leagues of Votann^^** units have the Hostile Acquisition ability. At the end of your Command phase, you can spend 3YP. If you do, ^^**Leagues of Votann^^** units from your army lose the Hostile Acquisition ability and gain the Fortify Takeover ability, or vice versa.')
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
