INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('bda5bb55-9158-9c97-916b-45d92dbbaf09', '6fb28a5d-6754-2d9a-ba2a-ec7b5a93e301', 'Atomic Disintegrators', 10, 'In your Shooting phase, each time the bearer’s unit is selected to shoot, when selecting an ability for the Technosorcerous Augmentations Detachment rule, you can also select from the following abilities: [ANTI‑MONSTER 5+], [ANTI‑VEHICLE 5+].');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('d3c5f285-71a9-42c2-ba20-9c84efbcee5b', '6fb28a5d-6754-2d9a-ba2a-ec7b5a93e301', 'Quantum Abacus', 15, 'Each time you select the bearer’s unit as the target of a Stratagem, roll one D6, adding 1 if it is within range of one or more objectives: on a 4+, you gain 1CP.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('34353ebe-5a20-09de-1a1c-a3857440d9bd', '6fb28a5d-6754-2d9a-ba2a-ec7b5a93e301', 'Gravitic Bolas', 15, 'In your Shooting phase, after the bearer has shot, select one enemy unit hit by one or more of those attacks (excluding ^^**Titanic^^** units); until the start of your next turn, that enemy unit is pinned. While a unit is pinned, subtract 2 from that unit’s Move characteristic and subtract 2 from Charge rolls made for that unit.');
