INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('6d3a62fe-a8f8-1255-b8ec-c06d8d6a4468', '010e6cf3-eb1c-94d8-ac64-cd252a9ffcf1', 'Murdermind', 15, 'The bearer has the Destroyer Cult keyword and during the Declare Battle Formations step, the bearer can be attached to a Destroyer Cult unit (excluding Character units). If you do, the bearer’s unit cannot contain any models without the Destroyer Cult keyword. Add 3" to the Move characteristic of the bearer.');
