INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('5f3efbe3-bfa3-d615-9c75-25595ae67557', 'b686c03c-f50a-5d2b-a411-5e63ca847dc5', 'Green Tide', 'Mob Mentality: Each time an attack targets a **^^Boyz^^** unit from your army, models in that unit have a 6+ invulnerable save against that attack. Each time an attack targets a **^^Boyz^^** unit from your army that contains 10 or more models, models in that unit have a 5+ invulnerable save against that attack.')
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('c34a8bf0-6444-4b95-d655-a2ff5fc4fdd7', '5f3efbe3-bfa3-d615-9c75-25595ae67557', 'Ferocious Show Off', 10, '**^^Orks Infantry^^** model only. Each time the bearer fights, while resolving those attacks, add 1 to the Strength characteristic of the bearer''s melee weapons. If the bearer''s unit contains 10 or more models, while resolving those attacks, add 3 to the Strength characteristic instead.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('34e7e785-430d-8531-b8fd-b29e9f9909ed', '5f3efbe3-bfa3-d615-9c75-25595ae67557', 'Brutal But Kunnin''', 25, '**^^Orks Infantry^^** model only. In your Command phase, if the bearer is on the battlefield (or is embarked within a **^^Transport^^** that is on the battlefield), roll one D6, adding 2 to the result if the bearer''s unit contains 10 or more models: on a 5+, you gain 1 CP.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('e546eefa-b1e8-c4c9-777f-7a69afe288df', '5f3efbe3-bfa3-d615-9c75-25595ae67557', 'Raucous Warcaller', 20, '**^^Orks Infantry^^** model only. While the bearer is leading a unit, that unit always counts as containing 10 or more models for the purposes of your Detachment rule and any Stratagems you use.');
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
