INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('48498841-8e7a-cd96-5f55-8387fdf92d4c', '0b4ac29e-5aac-4964-4573-4dfffac861c1', 'Kroot Hunting Pack', 'Hunter''s Instincts: Each time a KROOT model from your army makes an attack, add 1 to the Hit roll if the target of that attack is below its Starting Strength, and add 1 to the Wound roll as well if the target of that attack is Below Half-strength.
