INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('0b8cf09e-d6eb-b87d-3ec5-f4bd6fe38962', '48498841-8e7a-cd96-5f55-8387fdf92d4c', 'Root-carved Weapons', 10, 'KROOT WAR SHAPER model only. All weapons equipped by the bearer have the [PRECISION] and [DEVASTATING WOUNDS] abilities.');
