INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
  ('dfb719cc-9612-cf16-578d-5a216df803a5', '43db563b-9ea9-0d6c-2f1c-83bc1d947ed2', 'Enraged Reserves', 20, '**^^Tyranids Monster^^** only. If the bearer is destroyed by a melee attack, if it has not fought this phase, roll one D6: on a 3+, do not remove it from play. It can fight after the attacking model''s unit has finished making its attacks, and is then removed from play.');
INSERT INTO public.detachments (id, faction_id, name, rule_text) VALUES
  ('bce9f338-6e46-f044-eb54-a13e8d5cc5bb', '29048f43-09c7-cf02-c71b-6ef2dc3070b3', 'Synaptic Nexus', 'Synaptic Imperitives: At the start of the battle round, you can select one of the Synaptic Imperatives shown below. Until the end of the battle round, that Synaptic Imperative is active for your army and while a **^^Tyranids^^** unit from your army is within Synapse Range of your army, it will benefit from it. Each Synaptic Imperative can only be selected once per battle.
INSERT INTO public.enhancements (id, detachment_id, name, points, description) VALUES
